import AddIcon from "@mui/icons-material/Add";
import { getDownloadURL, ref, uploadBytes } from "firebase/storage";
import React, { useState } from "react";
import { storage } from "../../pages/Firebase";
import "./status.css";

const Status = ({ create, user }) => {
  const [file, setFile] = useState(null);
  const addStatus = async () => {
    const data = {
      userId: user._id,
      profilePic: user.profilePic,
      userstatus: user.username,
    };

    try {
      if (file) {
        const data = new FormData();
        const fileName = file.name + Date.now();
        data.append("name", fileName);
        data.append("file", file);

        const imgRef = ref(storage, `images/${file.name + Date.now()}`);

        uploadBytes(imgRef, file).then(() => {
          getDownloadURL(imgRef).then((url) => {
            data.statusPic = String(url);
            axiosInstance.post("/status", data).then(() => {
              window.location.reload();
            });
          });
          // console.log(newPost);
        });
      }
    } catch (err) {}
  };
  return (
    <div className="status">
      <div className="statusComponent">
        <img
          src="https://i.pinimg.com/736x/9c/44/5b/9c445b10fe9658a79f31fcdad62b12b8.jpg"
          alt=""
          className="statusProfile"
        />

        <div className="back">
          <img
            src="https://iphoneswallpapers.com/wp-content/uploads/2020/08/Burning-Rose-Flower.jpg"
            alt=""
          />
          {create == true && (
            <label htmlFor="file">
              <input
                style={{ display: "none" }}
                type="file"
                id="file"
                accept=".png,.jpeg,.jpg"
              />
              <AddIcon className="statusaddIcon" onClick={addStatus} />
            </label>
          )}
        </div>
      </div>
    </div>
  );
};

export default Status;
